package fs

import (
	"os"
	"path/filepath"
	"testing"

	. "github.com/smartystreets/goconvey/convey"
	"github.com/wtsi-ssg/wrstat/v6/combine"
)

//nolint:lll
const longFileContent = "!"

func TestFS(t *testing.T) {
	expectedOutput := longFileContent + "\n" + longFileContent + "\n"

	Convey("Given two files each containing a line over 65536 characters long, they can be concatenated and compressed.", t, func() {
		inputs, output, outputPath := buildTestFiles(t)

		err := combine.ConcatenateAndCompress(inputs, output, false)
		So(err, ShouldBeNil)

		_, err = os.Stat(outputPath)
		So(err, ShouldBeNil)

		Convey("And the compressed content can be read.", func() {
			actualContent, err := ReadCompressedFile(outputPath)
			So(err, ShouldBeNil)

			So(actualContent, ShouldEqual, expectedOutput)
		})
	})
}

// buildTestFiles builds two files, each with a line over 65536 chars long.
func buildTestFiles(t *testing.T) ([]*os.File, *os.File, string) {
	t.Helper()
	dir := t.TempDir()

	longfile1, err := os.Create(filepath.Join(dir, "longfile1"))
	So(err, ShouldBeNil)
	longfile2, err := os.Create(filepath.Join(dir, "longfile2"))
	So(err, ShouldBeNil)

	_, err = longfile1.WriteString(longFileContent)
	So(err, ShouldBeNil)
	_, err = longfile2.WriteString(longFileContent)
	So(err, ShouldBeNil)

	filenames, err := FindFilePathsInDir(dir, "")
	So(err, ShouldBeNil)

	inputs, err := OpenFiles(filenames)
	So(err, ShouldBeNil)

	outputPath := filepath.Join(dir, "output")

	fo, err := os.Create(outputPath)
	if err != nil {
		t.Fatalf("create error: %s", err)
	}

	return inputs, fo, outputPath
}
